//-00000000001a:13:{s:8:"weibo_id";s:4:"1462";s:3:"uid";s:5:"41139";s:7:"content";s:374:"《悟“平淡“》 人生一热一冷，一浓一淡，或许只有经历过大起大落，才能懂得淡的滋味。其实，生命的过程本来就很平淡，如一杯白开水，只是人们根据自己的喜好往里头加入了不同成分，于是产生了人生百味，酸甜苦辣尽在其中。 万般滋味，当它趋于平淡时，才是人生的真味。";s:5:"ctime";s:10:"1363743105";s:4:"from";s:1:"0";s:7:"comment";s:1:"0";s:12:"transpond_id";s:1:"0";s:9:"transpond";s:1:"0";s:4:"type";s:1:"0";s:9:"type_data";N;s:9:"from_data";N;s:5:"isdel";s:1:"0";s:5:"heart";s:1:"0";}
