<?php//微博点赞class HeartApi extends Api{	//获取当前用户的点赞列表	function index(){		return D('Heart','weibo')->getList($this->mid , $this->since_id , $this->max_id , $this->count , $this->page);	}	// 添加点赞	function create(){		return (int) D('Heart','weibo')->heartWeibo($this->id,$this->mid);	}	// 删除一个点赞信息	function destroy(){		return (int) D('Heart','weibo')->dodelete($this->id,$this->mid);	}	// 当前用户是否点赞了给定微博	function isHeart() {		return (int) D('Heart','weibo')->isHearted($this->id,$this->mid);	}}?>